import React, { useState, useEffect } from 'react';
import { 
  ChevronLeft, 
  FileText, 
  LayoutGrid,
  User, 
  LogOut,
  AlertCircle,
  CheckCircle,
  Loader
} from 'lucide-react';

// Mock API function for demonstration
const classifyComplaint = async (data) => {
  return new Promise((resolve) => {
    setTimeout(() => {
      resolve({
        complaint_id: `CMP-${Date.now()}`,
        category: 'Technical Issue',
        ...data
      });
    }, 1500);
  });
};

const Classify = ({ user, onLogout }) => {
  const [currentPage, setCurrentPage] = useState('classify');
  const [formData, setFormData] = useState({
    complaint_text: '',
    language: 'English'
  });
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [showSignupPrompt, setShowSignupPrompt] = useState(false);
  const [classificationResult, setClassificationResult] = useState(null);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
    setError('');
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!formData.complaint_text.trim()) {
      setError('Please enter a complaint text');
      return;
    }

    setError('');
    setLoading(true);
    setResult(null);

    try {
      const response = await classifyComplaint({
        ...formData,
        complaint_id: ''
      });
      
      // Store classification result
      setClassificationResult(response);
      
      // Show signup prompt if user is not logged in
      if (!user) {
        setShowSignupPrompt(true);
      } else {
        // If user is logged in, show results directly
        setResult(response);
      }
      setLoading(false);
    } catch (err) {
      setError(err.response?.data?.error || 'Classification failed. Please try again.');
      setLoading(false);
    }
  };

  const handleContinue = () => {
    // User clicked continue, show results
    setResult(classificationResult);
    setShowSignupPrompt(false);
  };

  const handleSignupRedirect = () => {
    // Store result in localStorage to show after signup
    localStorage.setItem('pendingResult', JSON.stringify(classificationResult));
    setCurrentPage('signup');
  };

  const handleLoginRedirect = () => {
    // Store result in localStorage to show after login
    localStorage.setItem('pendingResult', JSON.stringify(classificationResult));
    setCurrentPage('login');
  };

  // Check for pending result after login/signup
  useEffect(() => {
    const pendingResult = localStorage.getItem('pendingResult');
    if (pendingResult && user) {
      setResult(JSON.parse(pendingResult));
      localStorage.removeItem('pendingResult');
    }
  }, [user]);

  const handleSave = () => {
    setCurrentPage('dashboard');
  };

  const handleNewClassification = () => {
    setFormData({
      complaint_text: '',
      language: 'English'
    });
    setResult(null);
    setError('');
    setShowSignupPrompt(false);
    setClassificationResult(null);
  };

  const handleLogout = () => {
    onLogout();
    setCurrentPage('home');
  };

  return (
    <div className="min-h-screen bg-gray-50 flex">
      {/* Sidebar - Only show if user is logged in */}
      {user && (
        <div className="w-80 purple-gradient text-white p-6 flex flex-col">
          <div className="mb-8">
            <button 
              onClick={() => navigate('/dashboard')}
              className="text-white hover:text-purple-200 transition mb-4"
            >
              <ChevronLeft className="w-6 h-6" />
            </button>
            <h1 className="text-2xl font-bold">Complaint Analyzer</h1>
          </div>

          <nav className="flex-1 space-y-2">
            <button 
              onClick={() => navigate('/dashboard')}
              className="w-full flex items-center gap-3 px-4 py-3 hover:bg-white hover:bg-opacity-10 rounded-xl transition"
            >
              <LayoutGrid className="w-5 h-5" />
              <span className="font-medium">Dashboard</span>
            </button>
            <button className="w-full flex items-center gap-3 px-4 py-3 bg-white bg-opacity-20 rounded-xl backdrop-blur-sm">
              <FileText className="w-5 h-5" />
              <span className="font-medium">Classify Complaint</span>
            </button>
          </nav>

          <div className="mt-auto">
            <div className="bg-white bg-opacity-20 rounded-xl p-4 backdrop-blur-sm mb-4">
              <div className="flex items-center gap-3">
                <div className="w-10 h-10 bg-white bg-opacity-30 rounded-full flex items-center justify-center">
                  <User className="w-5 h-5" />
                </div>
                <div className="flex-1">
                  <p className="font-semibold text-sm">{user?.name}</p>
                  <p className="text-xs text-purple-100">{user?.email}</p>
                </div>
              </div>
            </div>
            <button
              onClick={handleLogout}
              className="w-full flex items-center justify-center gap-2 px-4 py-3 bg-red-500 hover:bg-red-600 rounded-xl transition font-medium"
            >
              <LogOut className="w-5 h-5" />
              Logout
            </button>
          </div>
        </div>
      )}

      {/* Main Content */}
      <div className={`flex-1 p-8 overflow-auto ${!user ? 'max-w-7xl mx-auto' : ''}`}>
        <div className="max-w-6xl mx-auto">
          {/* Header */}
          <div className="mb-8">
            {!user && (
              <button 
                onClick={() => setCurrentPage('home')}
                className="text-gray-600 hover:text-gray-800 transition mb-4 flex items-center gap-2"
              >
                <ChevronLeft className="w-5 h-5" />
                Back to Home
              </button>
            )}
            <h2 className="text-3xl font-bold text-gray-800 mb-2">Classify</h2>
            <p className="text-gray-600">Analyze and categorize your complaints</p>
          </div>

          <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
            {/* Left Side - Input Form */}
            <div className="bg-white rounded-2xl shadow-lg p-8">
              <form onSubmit={handleSubmit} className="space-y-6">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Complaint Text
                  </label>
                  <textarea
                    name="complaint_text"
                    value={formData.complaint_text}
                    onChange={handleChange}
                    rows="8"
                    className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:ring-2 focus:ring-primary focus:border-transparent outline-none resize-none"
                    placeholder="Enter your complaint text here..."
                    required
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Language
                  </label>
                  <select
                    name="language"
                    value={formData.language}
                    onChange={handleChange}
                    className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:ring-2 focus:ring-primary focus:border-transparent outline-none appearance-none bg-white"
                  >
                    <option value="English">English</option>
                    <option value="Hindi">Hindi</option>
                  </select>
                </div>

                {error && (
                  <div className="p-4 bg-red-50 border border-red-200 rounded-xl flex items-start gap-3">
                    <AlertCircle className="w-5 h-5 text-red-500 flex-shrink-0 mt-0.5" />
                    <p className="text-red-700 text-sm">{error}</p>
                  </div>
                )}

                <button
                  type="submit"
                  disabled={loading}
                  className="w-full btn-primary text-white font-semibold py-4 px-8 rounded-xl text-lg disabled:opacity-50 disabled:cursor-not-allowed flex items-center justify-center gap-2"
                >
                  {loading ? (
                    <>
                      <Loader className="w-5 h-5 animate-spin" />
                      Classifying...
                    </>
                  ) : (
                    'CLASSIFY COMPLAINT'
                  )}
                </button>
              </form>
            </div>

            {/* Right Side - Results or Signup Prompt */}
            <div className="bg-white rounded-2xl shadow-lg p-8">
              <div className="bg-gradient-to-br from-purple-400 via-purple-300 to-purple-200 rounded-t-2xl -m-8 mb-6 p-6 text-white">
                <h3 className="text-2xl font-bold">Results</h3>
              </div>

              {/* Signup Prompt */}
              {showSignupPrompt && !result && (
                <div className="space-y-6">
                  <div className="bg-blue-50 border border-blue-200 rounded-xl p-6 text-center">
                    <div className="w-16 h-16 bg-blue-100 rounded-full flex items-center justify-center mx-auto mb-4">
                      <CheckCircle className="w-8 h-8 text-blue-600" />
                    </div>
                    <h4 className="text-xl font-bold text-gray-800 mb-2">
                      Classification Complete!
                    </h4>
                    <p className="text-gray-600 mb-6">
                      Your complaint has been successfully analyzed. Please sign up or log in to view your results.
                    </p>
                    
                    <div className="space-y-3">
                      <button
                        onClick={handleSignupRedirect}
                        className="w-full bg-primary hover:bg-purple-700 text-white font-semibold py-3 px-6 rounded-xl transition"
                      >
                        Sign Up to View Results
                      </button>
                      <button
                        onClick={handleLoginRedirect}
                        className="w-full bg-gray-200 hover:bg-gray-300 text-gray-800 font-semibold py-3 px-6 rounded-xl transition"
                      >
                        Already have an account? Log In
                      </button>
                    </div>
                  </div>
                </div>
              )}

              {/* Results Display */}
              {result && (
                <div className="space-y-6">
                  <div className="bg-green-50 border border-green-200 rounded-xl p-4 flex items-start gap-3">
                    <CheckCircle className="w-6 h-6 text-green-600 flex-shrink-0 mt-0.5" />
                    <div>
                      <p className="font-semibold text-green-800 mb-1">Classification Successful!</p>
                      <p className="text-sm text-green-700">Your complaint has been analyzed and categorized.</p>
                    </div>
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-600 mb-2">
                      Complaint ID
                    </label>
                    <div className="flex items-center gap-3 px-4 py-3 bg-gray-50 rounded-xl">
                      <FileText className="w-5 h-5 text-gray-400" />
                      <span className="font-mono text-gray-700 font-semibold">
                        {result.complaint_id}
                      </span>
                    </div>
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-600 mb-2">
                      Category
                    </label>
                    <div className="px-4 py-4 bg-purple-50 border border-purple-200 rounded-xl">
                      <span className="text-xl font-bold text-primary capitalize">
                        {result.category}
                      </span>
                    </div>
                  </div>

                  <div className="flex gap-4 pt-4">
                    <button
                      onClick={handleSave}
                      className="flex-1 bg-secondary hover:bg-green-600 text-white font-semibold py-3 px-6 rounded-xl transition"
                    >
                      SAVE
                    </button>
                    <button
                      onClick={handleNewClassification}
                      className="flex-1 bg-gray-200 hover:bg-gray-300 text-gray-800 font-semibold py-3 px-6 rounded-xl transition"
                    >
                      NEW
                    </button>
                  </div>
                </div>
              )}

              {/* Empty State */}
              {!result && !showSignupPrompt && (
                <div className="text-center py-12">
                  <FileText className="w-16 h-16 text-gray-300 mx-auto mb-4" />
                  <p className="text-gray-500 mb-2">No classification yet</p>
                  <p className="text-sm text-gray-400">
                    Fill in the form and click "Classify Complaint" to see results here
                  </p>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Classify;
