import React, { useState, useEffect } from 'react';
import './App.css';
import GetStarted from './pages/GetStarted';
import VideoTutorial from './pages/VideoTutorial';
import Dashboard from './pages/Dashboard';
import Classify from './pages/Classify';

function App() {
  const [user, setUser] = useState(null);
  const [hasSeenVideo, setHasSeenVideo] = useState(false);
  const [currentView, setCurrentView] = useState('start');
  const [complaints, setComplaints] = useState([]);

  useEffect(() => {
    const loadData = async () => {
      try {
        const userResult = await window.storage.get('currentUser');
        const videoResult = await window.storage.get('hasSeenVideo');
        
        if (userResult) {
          const userData = JSON.parse(userResult.value);
          setUser(userData);
          loadComplaints(userData.id);
        }
        
        if (videoResult && videoResult.value === 'true') {
          setHasSeenVideo(true);
        }
      } catch (error) {
        console.log('No stored data found');
      }
    };

    loadData();
  }, []);

  const loadComplaints = async (userId) => {
    try {
      const result = await window.storage.get(`complaints_${userId}`);
      if (result) {
        setComplaints(JSON.parse(result.value));
      }
    } catch (error) {
      console.log('No complaints found');
    }
  };

  const handleLogin = async (userData) => {
    setUser(userData);
    await window.storage.set('currentUser', JSON.stringify(userData));
    await loadComplaints(userData.id);
    setCurrentView('dashboard');
  };

  const handleLogout = async () => {
    setUser(null);
    setComplaints([]);
    await window.storage.delete('currentUser');
    setCurrentView('start');
  };

  const markVideoAsSeen = async () => {
    setHasSeenVideo(true);
    await window.storage.set('hasSeenVideo', 'true');
    setCurrentView('dashboard');
  };

  const handleSaveComplaint = async (complaint) => {
    if (user) {
      const updatedComplaints = [...complaints, complaint];
      setComplaints(updatedComplaints);
      await window.storage.set(`complaints_${user.id}`, JSON.stringify(updatedComplaints));
    }
  };

  const navigate = (view) => {
    setCurrentView(view);
  };

  return (
    <div className="App">
      {currentView === 'start' && (
        <GetStarted onNext={() => setCurrentView('video')} />
      )}
      
      {currentView === 'video' && (
        <VideoTutorial 
          onComplete={markVideoAsSeen}
          onSkip={markVideoAsSeen}
        />
      )}
      
      {currentView === 'dashboard' && (
        <Dashboard 
          user={user}
          onLogout={handleLogout}
          onNavigate={navigate}
          complaints={complaints}
        />
      )}
      
      {currentView === 'classify' && (
        <Classify 
          user={user}
          onLogout={handleLogout}
          onNavigate={navigate}
          onLogin={handleLogin}
          onSaveComplaint={handleSaveComplaint}
        />
      )}
    </div>
  );
}

export default App;
