import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import './App.css';
import GetStarted from './pages/GetStarted';
import VideoTutorial from './pages/VideoTutorial';
import Signup from './pages/Signup';
import Login from './pages/Login';
import Dashboard from './pages/Dashboard';
import Classify from './pages/Classify';

function App() {
  const [user, setUser] = useState(null);
  const [hasSeenVideo, setHasSeenVideo] = useState(false);
  const [pendingClassification, setPendingClassification] = useState(null);

  useEffect(() => {
    const savedUser = localStorage.getItem('user');
    const seenVideo = localStorage.getItem('hasSeenVideo');
    
    if (savedUser) {
      setUser(JSON.parse(savedUser));
    }
    if (seenVideo) {
      setHasSeenVideo(true);
    }
  }, []);

  const handleLogin = (userData) => {
    setUser(userData);
    localStorage.setItem('user', JSON.stringify(userData));
  };

  const handleLogout = () => {
    setUser(null);
    localStorage.removeItem('user');
    setPendingClassification(null);
  };

  const markVideoAsSeen = () => {
    setHasSeenVideo(true);
    localStorage.setItem('hasSeenVideo', 'true');
  };

  return (
    <Router>
      <Routes>
        <Route path="/" element={<GetStarted />} />
        <Route 
          path="/video-tutorial" 
          element={
            hasSeenVideo ? <Navigate to="/classify" /> : 
            <VideoTutorial onComplete={markVideoAsSeen} />
          } 
        />
        <Route 
          path="/signup" 
          element={
            <Signup 
              onSignup={handleLogin} 
              pendingClassification={pendingClassification}
            />
          } 
        />
        <Route 
          path="/login" 
          element={
            <Login 
              onLogin={handleLogin}
              pendingClassification={pendingClassification}
            />
          } 
        />
        <Route 
          path="/dashboard" 
          element={
            user ? <Dashboard user={user} onLogout={handleLogout} /> : 
            <Navigate to="/login" />
          } 
        />
        <Route 
          path="/classify" 
          element={
            <Classify 
              user={user} 
              onLogout={handleLogout}
              onNeedAuth={(data) => setPendingClassification(data)}
            />
          } 
        />
      </Routes>
    </Router>
  );
}

export default App;
